        <span class="token class-name">String</span> remoteProblemFiles <span class="token operator">=</span> sshConfiguration<span class="token punctuation">.</span><span class="token function">getSshProperties</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">getRootDir</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">+</span> problemID <span class="token operator">+</span> <span class="token string">"/"</span><span class="token punctuation">;</span>
