     */</span>
    <span class="token annotation punctuation">@Override</span>
    <span class="token keyword">public</span> <span class="token keyword">void</span> <span class="token function">downloadProblemTestPointIOFiles</span><span class="token punctuation">(</span><span class="token class-name">String</span> problemID<span class="token punctuation">,</span> <span class="token class-name">String</span> testPointID<span class="token punctuation">,</span> <span class="token class-name">Path</span> local<span class="token punctuation">)</span> <span class="token keyword">throws</span> <span class="token class-name">SftpException</span> <span class="token punctuation">{</span>
        <span class="token class-name">String</span> remoteProblemFiles <span class="token operator">=</span> sshConfiguration<span class="token punctuation">.</span><span class="token function">getSshProperties</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">getRootDir</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">+</span> problemID <span class="token operator">+</span> <span class="token string">"/"</span> <span class="token operator">+</span> testPointID <span class="token operator">+</span> <span class="token string">"/"</span><span class="token punctuation">;</span>
        <span class="token class-name">ChannelSftp</span> sftp <span class="token operator">=</span> sshConfiguration<span class="token punctuation">.</span><span class="token function">getSftp</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token keyword">try</span> <span class="token punctuation">{</span>
            <span class="token class-name">List</span><span class="token generics"><span class="token punctuation">&lt;</span><span class="token class-name">String</span><span class="token punctuation">></span></span> list <span class="token operator">=</span> <span class="token function">listFileNamesInDir</span><span class="token punctuation">(</span>sftp<span class="token punctuation">,</span> remoteProblemFiles<span class="token punctuation">)</span><span class="token punctuation">;</span>
            <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token class-name">String</span> filePath <span class="token operator">:</span> list<span class="token punctuation">)</span> <span class="token punctuation">{</span>
                sftp<span class="token punctuation">.</span><span class="token function">get</span><span class="token punctuation">(</span>filePath<span class="token punctuation">,</span> local<span class="token punctuation">.</span><span class="token function">toString</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
